mapper.select:
/* getListUrbanGroup */ WITH RECURSIVE urban_group_tree ( urban_group_id, urban_group_name, user_id, ancestor, parent, depth, view_order, children, basic, available, start_date, end_date, viewStartDate, viewEndDate, location, area, receiving_population, receiving_household, project_operator, transfer_local_government, description, insert_date, path, cycle ) AS ( SELECT A.urban_group_id, A.urban_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.start_date, A.end_date, TO_CHAR(A.start_date, 'YYYY-MM-DD') AS viewStartDate, TO_CHAR(A.end_date, 'YYYY-MM-DD') AS viewEndDate, A.location, A.area, A.receiving_population, A.receiving_household, A.project_operator, A.transfer_local_government, A.description, A.insert_date, ARRAY[A.view_order], false FROM urban_group A WHERE A.parent = 0
AND A.available = #{available}
UNION ALL SELECT A.urban_group_id, A.urban_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.start_date, A.end_date, TO_CHAR(A.start_date, 'YYYY-MM-DD') AS viewStartDate, TO_CHAR(A.end_date, 'YYYY-MM-DD') AS viewEndDate, A.location, A.area, A.receiving_population, A.receiving_household, A.project_operator, A.transfer_local_government, A.description, A.insert_date, path || A.view_order, A.urban_group_id = ANY(path) FROM urban_group A, urban_group_tree B WHERE A.parent = B.urban_group_id AND not cycle ) SELECT urban_group_id, urban_group_name, user_id, ancestor, parent, depth, view_order, children, basic, available, start_date, end_date, viewStartDate, viewEndDate, location, area, receiving_population, receiving_household, project_operator, transfer_local_government, description, insert_date, path FROM urban_group_tree ORDER BY path

/* getUrbanGroupByParentAndViewOrder */
SELECT *
FROM urban_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

	ORDER BY view_order DESC



	AND view_order > #{viewOrder}

	ORDER BY view_order

LIMIT 1

mapper.update:
/* updateUrbanGroupViewOrder */
UPDATE urban_group
SET view_order = #{viewOrder}
WHERE urban_group_id = #{urbanGroupId}

mapper.delete:
/* deleteUrbanGroup */
DELETE
FROM urban_group
WHERE urban_group_id = #{urbanGroupId}

	AND user_id = #{userId}

/* deleteUrbanGroupByAncestor */
DELETE
FROM urban_group
WHERE ancestor = #{urbanGroupId}
OR urban_group_id = #{urbanGroupId}

	AND user_id = #{userId}

/* deleteUrbanGroupByParent */
DELETE
FROM urban_group
WHERE parent = #{urbanGroupId}
OR urban_group_id = #{urbanGroupId}

	AND user_id = #{userId}
